    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        Pessoa p = new Pessoa ();
        
        p.setNome ( txtNome.getText() );
        p.setEndereco ( txtEndereco.getText() );
        p.setIdade (Integer.parseInt (txtIdade .getText() ) );
        
        //JOptionPane.showMessageDialog (rootPane, p.getNome() );
        //JOptionPane.showMessageDialog (rootPane, p.getEndereco() );
        //JOptionPane.showMessageDialog (rootPane, p.getIdade() );
        // OU
        JOptionPane.showMessageDialog (rootPane, p.getNome() +"\n"+ p.getEndereco() +"\n"+ p.getIdade() );
    }//GEN-LAST:event_jButton1ActionPerformed

